from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor, UltrasonicSensor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
Brain = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
color_sensor = ColorSensor(Port.A)
distance_sensor = UltrasonicSensor(Port.B)
Left_Motor = Motor(Port.C, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.D, Direction.CLOCKWISE)
DriveTrain = DriveBase(Left_Motor, Right_Motor, 62, 94)
Right_Attachment = Motor(Port.F, Direction.CLOCKWISE)
Left_Attachment = Motor(Port.E, Direction.CLOCKWISE)

# Initialize variables.
Straight_Speed = 950
Straight_Acceleration = 925
Turn_Speed = 400
Turn_Acceleration = 700
Program_List = ['1', '2', '3', '4', '5', '6', '7', '8']
In_Leg = 1
Current_Program = 1

async def Start_Code():
    await wait(0)
    # This calabrates the robot to run the selected leg.
    DriveTrain.use_gyro(True)
    Brain.display.off()
    Brain.imu.reset_heading(0)
    Brain.light.on(Color.GREEN)
    Brain.display.pixel(0, 0, 100)
    Brain.display.pixel(1, 1, 80)
    Brain.display.pixel(2, 2, 60)
    Brain.display.pixel(3, 3, 40)
    Brain.display.pixel(4, 4, 10)

async def Code_Selecting():
    global Current_Program, In_Leg
    await wait(0)
    # This function allows you to choose a code to run on the robot without
    # the color sensor.
    if Button.RIGHT in Brain.buttons.pressed():
        Current_Program = Current_Program + 1
        await wait(350)
    elif Button.LEFT in Brain.buttons.pressed():
        Current_Program = Current_Program - 1
        await wait(350)
    elif Button.CENTER in Brain.buttons.pressed():
        if Current_Program == 1:
            Brain.light.on(Color.GREEN)
            await wait(300)
            await map2()
        elif Current_Program == 2:
            Brain.light.on(Color.GREEN)
            await wait(300)
            await silo()
        elif Current_Program == 3:
            Brain.light.on(Color.GREEN)
            await wait(300)
        elif Current_Program == 4:
            Brain.light.on(Color.GREEN)
            await wait(300)
        elif Current_Program == 5:
            Brain.light.on(Color.GREEN)
            await wait(200)
        elif Current_Program == 6:
            Brain.light.on(Color.GREEN)
            await wait(100)
        elif Current_Program == 7:
            Brain.light.on(Color.GREEN)
            await wait(100)
        elif Current_Program == 8:
            Brain.light.on(Color.GREEN)
            await wait(100)
        elif Current_Program == 9:
            Brain.light.on(Color.GREEN)
            await wait(100)
        else:
            Brain.light.on(Color.GREEN)
            await wait(100)
            await Battery()
    else:
        await wait(100)
    In_Leg = 0

async def Color_Select():
    await wait(0)
    # These allow for certain codes to start when the color sensor sees
    # a set color. It also ensures that we do not touch the robot when starting
    # a code.
    if await color_sensor.color() == Color.BLUE:
        Brain.light.on(Color.GREEN)
        await wait(450)
        await Go_Across()
    elif await color_sensor.color() == Color.YELLOW:
        Brain.light.on(Color.GREEN)
        await wait(450)
        await Boad_Dump()
    elif await color_sensor.color() == Color.GREEN:
        Brain.light.on(Color.GREEN)
        await wait(450)
        await Feed_the_whale()
    elif await color_sensor.color() == Color.WHITE:
        Brain.light.on(Color.GREEN)
        await wait(450)
    else:
        pass

async def map2():
    await wait(0)
    await Start_Code()
    await DriveTrain.straight(650)
    await DriveTrain.turn(-45)
    await DriveTrain.straight(100)

async def silo():
    await wait(0)
    await Start_Code()
    DriveTrain.settings(straight_acceleration=Straight_Acceleration - 250)
    await DriveTrain.straight(-20)
    DriveTrain.reset(0, 0)
    await DriveTrain.straight(465)
    await Right_Attachment.run_angle(700, 65)

async def Battery():
    await wait(0)
    # This allows us to check the battery voltage - to see
    # if it is charged.
    while True:
        await wait(0)
        print(Brain.battery.voltage())
        await wait(1500)

async def main1():
    DriveTrain.straight(0)
    Brain.imu.reset_heading(0)
    DriveTrain.settings(straight_speed=Straight_Speed)
    DriveTrain.settings(straight_acceleration=Straight_Acceleration)
    DriveTrain.settings(turn_rate=Turn_Speed)
    DriveTrain.settings(turn_acceleration=Turn_Acceleration)
    while True:
        await wait(0)
        # This is the main loop that the robot runs. It also displays the
        # code selected.
        Brain.display.number(Current_Program)
        Brain.light.on(Color.RED)
        await Code_Selecting()
        DriveTrain.stop()
        Left_Attachment.stop()
        Right_Attachment.stop()

async def main2():
    # If the robot is running a code, this stops and exits the program.
    while True:
        await wait(0)
        if In_Leg == 1:
            await wait(200)
            Brain.system.set_stop_button(Button.CENTER)
        else:
            Brain.system.set_stop_button(None)

async def main3():
    # This helps us to debug our code when needed.
    while True:
        await wait(0)
        await wait(100)


async def main():
    await multitask(main1(), main2(), main3())

run_task(main())