from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
HUB = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
Left_motor = Motor(Port.E, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.B, Direction.CLOCKWISE)
DRIVE_BASE = DriveBase(Left_motor, Right_Motor, 88, 128)

# Initialize variables.
ACTIVEPROGRAM = 0
SPEED = 620
ACELLERATION = 500
TURN_SPEED = 300
TURN_ACCELERATION = 500
PROGRAM_LIST = ['1', '2', '3', '4', '5']

def cart():
    DRIVE_BASE.straight(250)

def leg2():
    pass


# The main program starts here.
HUB.imu.reset_heading(0)
HUB.system.set_stop_button(None)
DRIVE_BASE.settings(straight_speed=SPEED)
DRIVE_BASE.settings(straight_acceleration=ACELLERATION)
DRIVE_BASE.settings(turn_rate=TURN_SPEED)
DRIVE_BASE.settings(turn_acceleration=TURN_ACCELERATION)
DRIVE_BASE.use_gyro(True)
while True:
    HUB.display.number(ACTIVEPROGRAM)
    HUB.light.on(Color.MAGENTA)
if Button.LEFT in HUB.buttons.pressed():
    ACTIVEPROGRAM = ACTIVEPROGRAM - 1
    wait(250)
elif Button.RIGHT in HUB.buttons.pressed():
    ACTIVEPROGRAM = ACTIVEPROGRAM + 1
    wait(100)
    cart()
elif Button.CENTER in HUB.buttons.pressed():
    HUB.light.on(Color.ORANGE)
    if ACTIVEPROGRAM == 1:
        wait(100)
        cart()
    elif ACTIVEPROGRAM == 2:
        pass
    elif True:
        pass
    elif True:
        pass
    elif True:
        pass
    else:
        pass
    wait(10)
else:
    pass
