from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Color, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
Brain = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
Left_Motor = Motor(Port.E, Direction.COUNTERCLOCKWISE)
Right_Motor = Motor(Port.B, Direction.CLOCKWISE)
DriveTrain = DriveBase(Left_Motor, Right_Motor, 62, 93.5)
RIGHT_ATTACHMENT = Motor(Port.D, Direction.CLOCKWISE)
LEFT_ATTACHMENT = Motor(Port.C, Direction.CLOCKWISE)

# Initialize variables.
Straight_Speed = 950
Straight_Acceleration = 800
Turn_Speed = 400
Turn_Acceleration = 675
Program_List = ['1', '2', '3', '4', '5']
In_Leg = 0
Current_Program = 1

async def Whale():
    await wait(1)
    await Start_Code()
    await DriveTrain.straight(-450, Stop.COAST)
    await DriveTrain.turn(25)
    await DriveTrain.straight(150)

async def Start_Task():
    global In_Leg
    await wait(1)
    In_Leg = 1
    Brain.display.off()
    Brain.display.pixel(1, 1, 30)
    Brain.display.pixel(2, 2, 50)
    Brain.display.pixel(3, 3, 70)

async def Start_Code():
    global In_Leg
    await wait(1)
    Brain.display.off()
    Brain.imu.reset_heading(0)
    Brain.light.on(Color.GREEN)
    In_Leg = 1
    Brain.display.pixel(0, 0, 100)
    Brain.display.pixel(1, 1, 80)
    Brain.display.pixel(2, 2, 60)
    Brain.display.pixel(3, 3, 40)
    Brain.display.pixel(4, 4, 10)

async def main1():
    global Current_Program, In_Leg
    Brain.imu.reset_heading(0)
    DriveTrain.settings(straight_speed=Straight_Speed)
    DriveTrain.settings(straight_acceleration=Straight_Acceleration)
    DriveTrain.settings(turn_rate=Turn_Speed)
    DriveTrain.settings(turn_acceleration=Turn_Acceleration)
    DriveTrain.straight(0)
    DriveTrain.use_gyro(True)
    while True:
        await wait(1)
        Brain.display.number(Current_Program)
        Brain.light.on(Color.MAGENTA)
        if Button.RIGHT in Brain.buttons.pressed():
            Current_Program = Current_Program + 1
            await wait(350)
        elif Button.LEFT in Brain.buttons.pressed():
            Current_Program = Current_Program - 1
            await wait(350)
        elif Button.CENTER in Brain.buttons.pressed():
            if Current_Program == 1:
                await pirit_ship()
                await wait(300)
            elif Current_Program == 2:
                await Whale()
                await wait(300)
            elif Current_Program == 3:
                await wait(300)
            elif Current_Program == 4:
                pass
            else:
                pass
        else:
            await wait(10)
        In_Leg = 0

async def main2():
    while True:
        await wait(1)
        if In_Leg == 1:
            await wait(200)
            Brain.system.set_stop_button(Button.CENTER)
        else:
            Brain.system.set_stop_button(None)

async def main3():
    while True:
        await wait(1)
        await wait(100)
        print(Brain.imu.heading(), LEFT_ATTACHMENT.angle(), RIGHT_ATTACHMENT.angle(), sep=", ")


async def main():
    await multitask(main1(), main2(), main3())

run_task(main())